sys_script.script:
var arr_fields = [];
var gr = new GlideRecord('sys_dictionary');
gr.addEncodedQuery('nameSTARTSWITHx_htaht_projects_projects_work_order^internal_type!=collection^ORinternal_type=NULL^element!=sys_id^ORelement=NULL');
gr.query();

while (gr.next()) {
    arr_fields.push(gr.getValue('element'));
}

// Querying records from x_htaht_projects_nw_pwo table
var nw_pwo_gr = new GlideRecord('x_htaht_projects_nw_pwo');
nw_pwo_gr.addQuery('sys_id', current.sys_id);
nw_pwo_gr.query();

while (nw_pwo_gr.next()) {
    var hopwo = new GlideRecord('x_htaht_projects_ho_pwo');
    hopwo.initialize();
    // Accessing field values for each record
    for (var i = 0; i < arr_fields.length; i++) {
        var field_name = arr_fields[i];
        var field_value = nw_pwo_gr.getValue(field_name);
        if (field_value !== null) {
            hopwo[field_name] = field_value;
        } else {
            gs.error("Field '" + field_name + "' not found or has null value in x_htaht_projects_nw_pwo table");
        }
    }
    hopwo.short_description = 'Automatically created from ' + current.number;
    hopwo.site = current.site;
    hopwo.nwpwo_reference = current.sys_id;
    hopwo.insert();
}
