function onLoad() {
    var ga = new GlideAjax('ProjectsWorkOrder');
    ga.addParam('sysparm_name', 'leftalignment');
    ga.getXML(DatParse);

    function DatParse(response) {
        var answer = response.responseXML.documentElement.getAttribute("answer");
        var newarr = answer.split(',');
        for (var i = 0; i < newarr.length; i++) {
            var fieldName = newarr[i].trim();
            var control = g_form.getControl(fieldName);
            if (control) {
                control.style.textAlign = "left";
            }
        }
    }
}
