sys_script_include.script:
var valPWOResolution = Class.create();
valPWOResolution.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	val : function() {

		var canIResolve = 'true';
		var sarsysid = this.getParameter('sysparm_sarsysid');
		var sar = new GlideRecord('x_quint_sar_site_access_request');
		sar.addQuery('sys_id', '=', sarsysid);
		sar.query();
		if (sar.next())
			{
if (sar.state == 1 || sar.state == 11 || sar.state == 12 || sar.state == 13 || sar.state == 14 || sar.state == 15 || sar.state == -5)
					{
						canIResolve = 'false';
					}
			}


	return canIResolve;
},

});
