sys_ui_policy.script_true:
function onCondition() {
    var projectType = g_form.getValue('project_type');
    var excludedFields = [];

    // Define excluded fields based on project type
        excludedFields = ['work_notes', 'project_type', 'additional_notes_regarding_alarms', 'additional_notes_regarding_backup_batteries', 'additional_notes_regarding_fire_safety', 'additional_notes_regarding_fire_site_earthing_and_protection', 'additional_notes_regarding_generator', 'additional_notes_regarding_hybrid_solar', 'additional_notes_regarding_rectifier_system', 'additional_notes_regarding_rms', 'additional_notes_regarding_security', 'additional_notes_regarding_site_access', 'additional_notes_regarding_tenant_info_and_labelling', 'additional_notes_regarding_the_general_site', 'additional_notes_regarding_the_grid_connection', 'additional_notes_regarding_the_tower_structure', 'short_description', 'description', 'assignment_group', 'assigned_to', 'project_manager', 'u_site_access_request'];

    // Set all editable fields to mandatory except for excluded fields
    var fields = g_form.getEditableFields();
    for (var i = 0; i < fields.length; i++) {
        var fieldName = fields[i];
        if (excludedFields.indexOf(fieldName) === -1) { // Check if the field is not in the excluded list
            g_form.setMandatory(fieldName, true);
        }
    }
}
