sys_ui_policy.script_true:
function onCondition() {
    var currentState = g_form.getValue('state'); // Assuming 'state' is the field name for the state

    var mandatoryFields = ['generator_details', 'rectifier_details', 'rms_details', 'tenant_details', 'backup_details', 'oss_details', 'site_earthing_details', 'fire_safety_details', 'site_access_details', 'fence_security_details', 'tower_details', 'grid_power_details', 'hybrid_solar_details', 'site_general_details'];

    if (currentState === '2' || currentState === '-2') { // Check if the state is 2 or -2
        // Set mandatory fields
        for (var i = 0; i < mandatoryFields.length; i++) {
            var fieldName = mandatoryFields[i];
            g_form.setMandatory(fieldName, true);
        }
    }

    var excludedFields = ['generator_details', 'rectifier_details', 'rms_details', 'tenant_details', 'backup_details', 'oss_details', 'site_earthing_details', 'fire_safety_details', 'site_access_details', 'fence_security_details', 'tower_details', 'grid_power_details', 'hybrid_solar_details', 'site_general_details', 'short_description', 'description', 'assignment_group', 'assigned_to']; // List of fields to exclude

    // Set all editable fields to read-only except excluded fields
    var fields = g_form.getEditableFields();
    for (var i = 0; i < fields.length; i++) {
        var fieldName = fields[i];
        if (excludedFields.indexOf(fieldName) === -1) { // Check if the field is not in the excluded list
            g_form.setReadOnly(fieldName, true);
        }
    }
}
